sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	//Questa variabile viene usata come cache, per recuperare il valore già presente sul campo
	var whatNeed=g_form.getValue('u_what_needed');

	//elimino tutte le opzioni dal campo u_what_needed
	g_form.clearOptions('u_what_needed');

	//Per ogni valore del campo "Tipo Richiesta" vengono definiti i possibili valori del campo "Di cosa hai bisogno"
	if (newValue=='hr'){
		g_form.addOption('u_what_needed','','-- None --');
		g_form.addOption('u_what_needed','hr1','Human Resources 1');
		g_form.addOption('u_what_needed','hr2','Human Resources 2');
		g_form.addOption('u_what_needed','other','Other');
	}
	if (newValue=='facilities'){
		g_form.addOption('u_what_needed','','-- None --');
		g_form.addOption('u_what_needed','facilities1','Facilities 1');
		g_form.addOption('u_what_needed','facilities2','Facilities 2');
		g_form.addOption('u_what_needed','other','Other');
	}
	if (newValue=='legal'){
		g_form.addOption('u_what_needed','','-- None --');
		g_form.addOption('u_what_needed','legal1','Legal 1');
		g_form.addOption('u_what_needed','legal2','Legal 2');
		g_form.addOption('u_what_needed','other','Other');
	}
	if (newValue=='your_dep_choice'){
		g_form.addOption('u_what_needed','','-- None --');
		g_form.addOption('u_what_needed','your_dep_choice1','Your Department Choice 1');
		g_form.addOption('u_what_needed','your_dep_choice2','Your Department Choice 2');
		g_form.addOption('u_what_needed','other','Other');
	}
	if (newValue===''){
		g_form.addOption('u_what_needed','','-- None --');
	}

	//Se il ticket non è in fase di creazione deve essere caricato il valore corrente del campo "Di cosa hai bisogno"
	if(isLoading &&!g_form.isNewRecord()) {
		g_form.setValue('u_what_needed',whatNeed);
		alert("campo rivalorizzato");
	}
}
